const puppeteer = require('puppeteer');
(async () => {
  const html = \`
  <!DOCTYPE html>
  <html lang='ru'>
  <head>
    <meta charset='UTF-8' />
    <link href='https://fonts.googleapis.com/css2?family=Montserrat:wght@900&display=swap' rel='stylesheet'>
    <style>
      html, body { margin: 0; padding: 0; }
      .banner {
        position: relative;
        width: 1200px; height: 630px;
        background-image: url('https://i.ibb.co/KjH7tTvP/Chat-GPT-Image-30-2025-16-55-34.png');
        background-size: cover;
        background-position: center;
        font-family: 'Montserrat', sans-serif;
      }
      .safe-area {
        position: absolute;
        top: 100px; left: 10px;
        width: 700px; height: 330px;
        padding: 20px; box-sizing: border-box;
        display: flex; align-items: center; justify-content: center;
        overflow: hidden;
      }
      .text {
        color: white; font-weight: 900;
        text-align: center;
        text-shadow: 2px 2px 4px rgba(0,0,0,0.7);
        display: inline-block;
        width: 100%;
        line-height: 1.1;
        word-break: break-word;
        white-space: normal;
      }
    </style>
  </head>
  <body>
    <div class='banner'>
      <div class='safe-area'>
        <div class='text' id='text'>
          КАК ПРАВИЛЬНО ИСПОЛЬЗОВАТЬ ПОЗИЦИЮ ЗА СТОЛОМ?
        </div>
      </div>
    </div>
    <script>
      function fitText(elId, min = 10, max = 200) {
        const el = document.getElementById(elId);
        const container = el.parentElement;
        let fontSize = min;
        el.style.fontSize = fontSize + 'px';
        while (el.scrollHeight <= container.clientHeight &&
               el.scrollWidth <= container.clientWidth &&
               fontSize < max) {
          fontSize++;
          el.style.fontSize = fontSize + 'px';
        }
        if (el.scrollHeight > container.clientHeight ||
            el.scrollWidth > container.clientWidth) {
          fontSize--;
          el.style.fontSize = fontSize + 'px';
        }
      }
      window.addEventListener('load', () => fitText('text'));
      window.addEventListener('resize', () => fitText('text'));
    </script>
  </body>
  </html>\`;

  const browser = await puppeteer.launch({
    headless: true,
    args: ['--no-sandbox'],
    executablePath: process.env.PUPPETEER_EXECUTABLE_PATH || '/usr/bin/chromium'
  });

  const page = await browser.newPage();
  await page.setViewport({ width: 1200, height: 630 });
  await page.setContent(html, { waitUntil: 'networkidle0' });
  await new Promise(r => setTimeout(r, 500));
  const buffer = await page.screenshot({ type: 'jpeg' });
  process.stdout.write(buffer.toString('base64'));
  await browser.close();
  await new Promise(resolve => setTimeout(resolve, 15000));
})();
"

